mapper.select:
      SELECT * FROM movie
      ORDER BY movie_rank
		limit #{startRow}, #{pageSize}

mapper.insert:
INSERT INTO movie (
  movie_cd, movie_nm, movie_nm_en, open_dt, poster_url, summary,
  show_tm, directors, actors, genre_nm, nation_nm, watch_grade_nm, show_type
) VALUES (
  #{movieCd}, #{movieNm}, #{movieNmEn}, #{openDt}, #{posterUrl}, #{summary},
  #{showTm}, #{directors}, #{actors}, #{genreNm}, #{nationNm}, #{watchGradeNm}, #{showType}
)
